import { convertCopticText } from "./index.mjs";
 readline = require('readline').createInterface({
  input: process.stdin,
  output: process.stdout
});

readline.question('Please provide the text you want to convert', async text => {
  let Athanasius = await convertCopticText(
    "ATHANASIUS",
    text,
    "COMBINE_WITH_CHAR_AFTER"
  );
  console.log("convertedText: ", Athanasius);
})


